CREATE VIEW [PRD].[VW_C_METRICS_NOTIFICATION] AS (
SELECT 1 ID_METRICS_NOTIFICATION,  'NOMINAL_VALUE' TYPE_VALIDATION		 	,'The reading value of  @NM_METRICS (@METRICS_VALUE) is different of nominal value (@NOMINAL_VALUE) in the w.o @WO , Line @LINE' DS_BODY  UNION ALL
SELECT 2 ID_METRICS_NOTIFICATION,  'UPPER_VALUE' TYPE_VALIDATION			,'The reading value of  @NM_METRICS (@METRICS_VALUE) is upper than the top value allowed (@UPPER_VALUE) in the w.o @WO , Line @LINE' DS_BODY UNION ALL
SELECT 3 ID_METRICS_NOTIFICATION,  'LOWER_VALUE' TYPE_VALIDATION			,'The reading value of  @NM_METRICS (@METRICS_VALUE) is lower than the bottom value allowed (@LOWER_VALUE) in the w.o @WO , Line @LINE' DS_BODY UNION ALL
SELECT 4 ID_METRICS_NOTIFICATION,  'ABSOLUTE_HYSTERESIS' TYPE_VALIDATION 	,'The reading value of  @NM_METRICS (@METRICS_VALUE) is different at nominal value (@NOMINAL_VALUE)' DS_BODY UNION ALL
SELECT 5 ID_METRICS_NOTIFICATION,  'RELATIVE_HYSTERESIS' TYPE_VALIDATION 	,'The reading value of  @NM_METRICS (@METRICS_VALUE) is different at nominal value (@NOMINAL_VALUE)' DS_BODY UNION ALL
SELECT 6 ID_METRICS_NOTIFICATION,  'PORCENTAGE_HYSTERESIS' TYPE_VALIDATION  ,'The reading value of  @NM_METRICS (@METRICS_VALUE) is different at nominal value (@NOMINAL_VALUE)' DS_BODY)